frappe.ui.form.on('BOM Item', {

	area:function(frm,cdt,cdn){
		var d = locals[cdt][cdn];
		var area = (flt(d.length) * flt(d.width))  ;
		frappe.model.set_value(cdt, cdn, "area", area);
		var qty = (flt(d.area) * flt(d.pcs))  ;
