		frappe.model.set_value(cdt, cdn, 'total_amount', total);
	},
	rate:function(frm,cdt,cdn){
		var d = locals[cdt][cdn];
		var gst_amount = (flt(d.qty) * flt(d.rate)) * flt(d.gst) / 100 ;
